export const className = (...cls) => cls.filter(Boolean).join(" ")

/**
 * 根据cookie名称获取value
 */
export function getCookie(key){
  const str = document.cookie || ""
  const arr = str.split(";")
  for(const item of arr){
    const [k, v] = item.split("=")
    if(k === key){
      return v
    }
  }
  return ""
}

/**
 * 根据指定keys获取cookie对象，如果keys为空，则获取所有cookie
 */
export function getCookies(...keys){
  const str = document.cookie || ""
  const arr = str.split(";")
  const res = {}
  for(const item of arr){
    const [k, v] = item.split("=")
    if(!keys.length || keys.includes(k)){
      res[k] = v
    }
  }
  return res
}

